					SendNotifyMsg(NULL, sTargetH, NOTIFY_MAGICEFFECTOFF, MAGICTYPE_HOLDOBJECT, m_pClientList[sTargetH]->m_cMagicEffectStatus[ MAGICTYPE_HOLDOBJECT ], NULL, NULL);

