void CGame::SendItemNotifyMsg(int iClientH, WORD wMsgType, CItem *pItem, int iV1, bool deleteOnError)
{
	char  * cp, cData[512];
	DWORD * dwp;
	WORD  * wp;
	int     iRet;
	short *sp;

	if (m_pClientList[iClientH] == NULL) return;

	dwp  = (DWORD *)(cData + INDEX4_MSGID);
	*dwp = MSGID_NOTIFY;
	wp   = (WORD *)(cData + INDEX2_MSGTYPE);
	*wp  = wMsgType;
	cp = (char *)(cData + INDEX2_MSGTYPE + 2);

	switch (wMsgType)
	{
	case NOTIFY_ITEMOBTAINED:
		WriteItemData(cp, pItem);

		dwp = (DWORD *)cp;
		*dwp = pItem->m_dwAttribute;
		cp += 4;

		dwp = (DWORD *)cp;
		*dwp = pItem->m_dwTempItemAttr;
		cp += 4;

		sp = (short *)cp;
		*sp = (char)pItem->m_sItemSpecEffectValue2; 
		cp += 2;
		
		for(int j = 0; j < MAXITEMSOCKETS; j++) Push(cp, pItem->m_sockets[j]);

		iRet = m_pClientList[iClientH]->m_pXSock->iSendMsg(cData, 26 + MAXITEMSOCKETS);
		break;

	case NOTIFY_ITEMPURCHASED:
		WriteItemData(cp, pItem);

		wp  = (WORD *)cp;
		*wp = iV1; // (iCost - iDiscountCost);

		iRet = m_pClientList[iClientH]->m_pXSock->iSendMsg(cData, 18);
		break;

	case NOTIFY_CANNOTCARRYMOREITEM:
		iRet = m_pClientList[iClientH]->m_pXSock->iSendMsg(cData, 6);
		break;
	}

	if (deleteOnError){
		switch (iRet) {
		case XSOCKEVENT_QUENEFULL:
		case XSOCKEVENT_SOCKETERROR:
		case XSOCKEVENT_CRITICALERROR:
		case XSOCKEVENT_SOCKETCLOSED:
					PutLogList("test 32");
			DeleteClient(iClientH, TRUE, TRUE);
			return;
		}
	}
}

