void CGame::AdminOrder_Summon(int iClientH, char *pData, DWORD dwMsgSize)
{
	char   seps[] = "= \t\n";
	char   * token, cBuff[256], cName_Master[NPCNAMELENGTH], cName_Slave[NPCNAMELENGTH], cNpcName[256], cWaypoint[11], cSA; 
	int    pX, pY, j, iNum, iNamingValue;
	bool   bMaster, bSummoned, isBerserked;

	if (m_pClientList[iClientH] == NULL) return;
	if ((dwMsgSize)	<= 0) return;

	if (m_pClientList[iClientH]->m_iAdminUserLevel < 3) {

		SendNotifyMsg(NULL, iClientH, NOTIFY_ADMINUSERLEVELLOW, NULL, NULL, NULL, NULL);
		return;
	}

	ZeroMemory(cBuff, sizeof(cBuff));
	memcpy(cBuff, pData, dwMsgSize);

	StrTok pStrTok(new CStrTok(cBuff, seps));
	token = pStrTok->pGet();
	token = pStrTok->pGet();

	if (token == NULL) {
		return;
	}

	ZeroMemory(cNpcName, sizeof(cNpcName));
	strcpy(cNpcName, token);

	token = pStrTok->pGet();

	if (token != NULL) 
		iNum = atoi(token);
	else iNum = 1;

	if (iNum <= 0)  iNum = 1;
	if (iNum >= 50)  iNum = 50;

	bSummoned = FALSE;
	token = pStrTok->pGet();
	if (token != NULL)
		if (token[0]=='1')bSummoned = TRUE;

	token = pStrTok->pGet();
	if (token != NULL){
		cSA = atoi(token);
		if(cSA < 0 || cSA > 10){
			cSA = 0;
		}
	} else cSA = 0;

	isBerserked = FALSE;
	token = pStrTok->pGet();
	if (token != NULL && token[0]=='1') {
		isBerserked = TRUE;
	}

	pX = m_pClientList[iClientH]->m_sX;
	pY = m_pClientList[iClientH]->m_sY;

	/*wsprintf(g_cTxt, "(!) Admin Order: Summon(%s)-(%d)", cNpcName, iNum);
	PutLogList(g_cTxt);*/

	wsprintf(g_cTxt, "GM Order(%s): Summon NPC(%s)-Count(%d)",m_pClientList[iClientH]->m_cCharName, cNpcName, iNum);
	bSendMsgToLS(MSGID_GAMEMASTERLOG, iClientH, NULL,g_cTxt);

	iNamingValue = m_pMapList[m_pClientList[iClientH]->m_cMapIndex]->GetEmptyNamingValue(cName_Master, m_pClientList[iClientH]->m_cMapIndex);
	if (iNamingValue != -1) {

		if ((bMaster = bCreateNewNpc( cNpcName, cName_Master, m_pClientList[iClientH]->m_cMapIndex, cSA, MOVETYPE_RANDOM, &pX, &pY, cWaypoint, NULL, NULL, -1, FALSE, bSummoned, isBerserked, TRUE)) == FALSE) {
			m_pMapList[m_pClientList[iClientH]->m_cMapIndex]->SetNamingValueEmpty(iNamingValue);
		}
	}

	for (j = 0; j < (iNum - 1); j++) {
		iNamingValue = m_pMapList[m_pClientList[iClientH]->m_cMapIndex]->GetEmptyNamingValue(cName_Slave, m_pClientList[iClientH]->m_cMapIndex);
		if (iNamingValue != -1) {
			if (bCreateNewNpc( cNpcName, cName_Slave, m_pClientList[iClientH]->m_cMapIndex, cSA, MOVETYPE_RANDOM, &pX, &pY, cWaypoint, NULL, NULL, -1, FALSE, bSummoned, isBerserked) == FALSE) {
				m_pMapList[m_pClientList[iClientH]->m_cMapIndex]->SetNamingValueEmpty(iNamingValue);
				break;
			}
			else {
				bSetNpcFollowMode(cName_Slave, cName_Master, OWNERTYPE_NPC);
			}
		}
	}

}


