void CGame::UpgradeHeroSetHandler(int iClientH, char * pData, DWORD dwMsgSize)
{	
	char *cp, cCurTier, cSetType, cSetItem[4], cAdd;
	int i;
	DWORD dwTemp;
	char G_cTxt[128];

	if (m_pClientList[iClientH] == NULL) return;
	if (m_pClientList[iClientH]->m_bIsInitComplete == FALSE) return;
	
	cp = (char *)(pData + INDEX2_MSGTYPE + 2);
	cCurTier = *cp;
	cp++;
	cSetType = *cp;
	cp++;
	
	if (m_pClientList[iClientH]->m_iEnemyKillCount < 250*(2^(cCurTier-1))) {
		SendNotifyMsg(NULL, iClientH, NOTIFY_ITEMUPGRADEFAIL, 2, NULL, NULL, NULL);
		return;
	}

	for(i = 0; i < 4; i++) cSetItem[i] = -1;
	
	if (m_pClientList[iClientH]->m_side == ELVINE) cAdd = 2;
	else cAdd = 0;

	for (i = 0; i < MAXITEMS; i++)
	if (m_pClientList[iClientH]->m_pItemList[i] != NULL) {
		if (((m_pClientList[iClientH]->m_pItemList[i]->m_dwAttribute & 0x0F000000) >> 24) == cCurTier-1) {
			if (m_pClientList[iClientH]->m_pItemList[i]->m_sIDnum == 419+m_pClientList[iClientH]->m_cSex-1+cAdd) cSetItem[0] = i;
			if (m_pClientList[iClientH]->m_pItemList[i]->m_sIDnum == 423+m_pClientList[iClientH]->m_cSex-1+cAdd) cSetItem[1] = i;
			if (cSetType == 0) {
				if (m_pClientList[iClientH]->m_pItemList[i]->m_sIDnum == 403+m_pClientList[iClientH]->m_cSex-1+cAdd) cSetItem[2] = i;
				if (m_pClientList[iClientH]->m_pItemList[i]->m_sIDnum == 411+m_pClientList[iClientH]->m_cSex-1+cAdd) cSetItem[3] = i;
			}else if (cSetType == 1) {
				if (m_pClientList[iClientH]->m_pItemList[i]->m_sIDnum == 407+m_pClientList[iClientH]->m_cSex-1+cAdd) cSetItem[2] = i;
				if (m_pClientList[iClientH]->m_pItemList[i]->m_sIDnum == 415+m_pClientList[iClientH]->m_cSex-1+cAdd) cSetItem[3] = i;
			}else {
				if (m_pClientList[iClientH]->m_pItemList[i]->m_sIDnum == 431+m_pClientList[iClientH]->m_cSex-1+cAdd) cSetItem[2] = i;
				if (m_pClientList[iClientH]->m_pItemList[i]->m_sIDnum == 435+m_pClientList[iClientH]->m_cSex-1+cAdd) cSetItem[3] = i;
			}
		}
	}

	if (cSetItem[0] != -1 && cSetItem[1] != -1 && cSetItem[2] != -1 && cSetItem[3] != -1) {
		for(i = 0; i < 4; i++) {
			if ((m_pClientList[iClientH]->m_pItemList[cSetItem[i]]->m_sTouchEffectValue1 != m_pClientList[iClientH]->m_sCharIDnum1) 
				|| (m_pClientList[iClientH]->m_pItemList[cSetItem[i]]->m_sTouchEffectValue2 != m_pClientList[iClientH]->m_sCharIDnum2) 
				|| (m_pClientList[iClientH]->m_pItemList[cSetItem[i]]->m_sTouchEffectValue3 != m_pClientList[iClientH]->m_sCharIDnum3)) 
			{
				SendNotifyMsg(NULL, iClientH, NOTIFY_ITEMUPGRADEFAIL, 2, NULL, NULL, NULL);
				return;
			}
		}

		for(i = 0; i < 4; i++) {
			dwTemp = m_pClientList[iClientH]->m_pItemList[cSetItem[i]]->m_dwAttribute;
			dwTemp = dwTemp & 0xF0FFFFFF;
			m_pClientList[iClientH]->m_pItemList[cSetItem[i]]->m_dwAttribute = dwTemp | (cCurTier << 24);
			m_pClientList[iClientH]->m_pItemList[cSetItem[i]]->m_cItemColor = 16+cCurTier+cAdd;
			
				SendNotifyMsg(NULL, iClientH, NOTIFY_GIZONEITEMCHANGE, cSetItem[i], m_pClientList[iClientH]->m_pItemList[cSetItem[i]]->m_cItemColor,
						m_pClientList[iClientH]->m_pItemList[cSetItem[i]]->m_wCurLifeSpan, NULL,
						m_pClientList[iClientH]->m_pItemList[cSetItem[i]]->m_dwAttribute,
						m_pClientList[iClientH]->m_pItemList[cSetItem[i]]->m_sItemSpecEffectValue2);
		}

		m_pClientList[iClientH]->m_iEnemyKillCount -= 250*(2^(cCurTier-1));
		SendNotifyMsg(NULL, iClientH, NOTIFY_ENEMYKILLS, m_pClientList[iClientH]->m_iEnemyKillCount, NULL, NULL, NULL);
		SendNotifyMsg(NULL, iClientH, NOTIFY_MAXENEMYKILLS, m_pClientList[iClientH]->m_iEnemyKillMaxCount, NULL, NULL, NULL);
	}
}
