	char *cp, cItemID, sendData[200];
	int *ip, iMinBidAmount, iBuyoutAmount, iEraseReq;
	DWORD *dwp, dwCount, goldCount;
	CItem *pItem;
	
	if (m_pClientList[iClientH] == NULL) return;
	if (!m_pClientList[iClientH]->m_bIsInitComplete) return;
	if (memcmp(m_pClientList[iClientH]->m_cMapName, "wrhus", 5) != 0) return;

	cp = (char *)(pData + INDEX2_MSGTYPE + 2);
	cItemID = *cp;
	cp++;
	ip = (int *)cp;
	iMinBidAmount = *ip;
	cp += 4;
	ip = (int *)cp;
	iBuyoutAmount = *ip;
	cp += 4;
	dwp = (DWORD *)cp;
	dwCount = *dwp;
	cp += 4;
	
