void CGame::CheckMagicInt(int iClientH, bool bDisable)
{
	bool changed = false;
	//if(m_pClientList[iClientH]->IsGM()) return false;
	for(int i = 0; i < MAXMAGICTYPE; i++)
	{
		if(m_pMagicConfigList[i] != NULL) {
			if(m_pClientList[iClientH]->m_cMagicMastery[i] != NULL && m_pMagicConfigList[i]->m_sIntLimit > m_pClientList[iClientH]->GetInt())
			{
				if (bDisable) m_pClientList[iClientH]->m_cMagicMastery[i] = -1;
				else m_pClientList[iClientH]->m_cMagicMastery[i] = 0;
				changed = true;
			}
			else if (m_pClientList[iClientH]->m_cMagicMastery[i] == -1 && m_pMagicConfigList[i]->m_sIntLimit <= m_pClientList[iClientH]->GetInt()) {
				m_pClientList[iClientH]->m_cMagicMastery[i] = 1;
				changed = true;
			}
		}
	}

	if (changed) SendNotifyMsg(NULL, iClientH, NOTIFY_SPELL_SKILL, NULL, NULL, NULL, NULL);
}

