void CGame::GuildContribute(CClient * player, char * data)
{
	if(!player->m_guild)
		return;

	uint8 type;
	Pop(data, type);
	uint32 amount;
	Pop(data, amount);

	switch(type)
	{
	case 0:
		player->m_guild->ContributeGold(player, amount);
		break;
	case 1:
		player->m_guild->ContributeMaj(player, amount);
		break;
	case 2:
		player->m_guild->ContributeCont(player, amount);
		break;
	}
}

