void CGame::TileCleaner()
{
	class CItem * pItem[ITEMS_PER_TILE] = {NULL};
	short sNextIDnum;
	char  cNextItemColor;
	BOOL bReplaceSprite;
	time_t now = time(NULL);
	DWORD dwNextItemAttribute;

	for(int i = 0; i < MAXGROUNDITEMS; i++)
	{
		if(m_stGroundNpcItem[i].bEmpty || 
			m_stGroundNpcItem[i].dropTime > now)	
			continue;

		m_stGroundNpcItem[i].bEmpty = TRUE;
		bReplaceSprite = TRUE;
		sNextIDnum = cNextItemColor = dwNextItemAttribute = 0;

		for(int j = 0; j < ITEMS_PER_TILE; j++)
		{
			pItem[j] = m_pMapList[m_stGroundNpcItem[i].cMapIndex]->pGetItem(m_stGroundNpcItem[i].sx, m_stGroundNpcItem[i].sy, &sNextIDnum, &cNextItemColor, &dwNextItemAttribute);

			if(!pItem[j]) 
				continue;
			else if(pItem[j] == m_stGroundNpcItem[i].item)
			{
				delete pItem[j];
				pItem[j] = NULL;
				continue;
			}
			else if(bReplaceSprite)
				bReplaceSprite = FALSE;
		}

		for(int j=ITEMS_PER_TILE; j; j--)
		{
			if(pItem[j-1])
				m_pMapList[m_stGroundNpcItem[i].cMapIndex]->bSetItem(m_stGroundNpcItem[i].sx, m_stGroundNpcItem[i].sy, pItem[j-1]);
		}

		if(bReplaceSprite)
		{
			SendEventToNearClient_TypeB(MSGID_EVENT_COMMON, COMMONTYPE_SETITEM, m_stGroundNpcItem[i].cMapIndex,
				m_stGroundNpcItem[i].sx, m_stGroundNpcItem[i].sy,
				sNextIDnum, cNextItemColor, NULL, dwNextItemAttribute);
		}
	}
}

void CGame::AddGroundItem(CItem * pItem, short x, short y, char mapIndex, DWORD dwTime)
{
	//if (pItem->m_cTouchEffectType == ITET_UNIQUE_OWNER) return;

	for(int i = 0; i < MAXGROUNDITEMS; i++)
	{
		if(m_stGroundNpcItem[i].bEmpty)
		{
			m_stGroundNpcItem[i].bEmpty = FALSE;
			m_stGroundNpcItem[i].dropTime = time(NULL) + dwTime;
			m_stGroundNpcItem[i].item = pItem;
			m_stGroundNpcItem[i].sx = x;
			m_stGroundNpcItem[i].sy = y;
			m_stGroundNpcItem[i].cMapIndex = mapIndex;
			return;
		}
	}
	PutLogList("WARNING: Ground NPC item list is full, item will not be cleared.");
}

void CGame::HandleLegionService(char * data)
{	char *cp, sex, cNewName[11];
	WORD *wp;

	WORD cmd = *(WORD *)(data+4);
	cp = (char *)(data+6);
	char type = *cp;
	cp++;
	if (cmd == CMD_LGNSVC_NAMECHANGE) {
		memcpy(cNewName, cp, 10);
		cp += 10;
	}else {
		sex = *cp;
		cp++;
	}
	wp = (WORD *)cp;
	WORD clientH = *wp;
	cp += 2;

	CClient * player;
	player = m_pClientList[clientH];

	if (!player || !player->m_bIsInitComplete || (memcmp(player->m_cMapName, "cityhall", 8) != 0)) return;

	if (type >= 4) return;

	int i=0;
	while(lgnPtsSvcs[type][i].price && 
		lgnPtsSvcs[type][i].cmd != cmd) 
	{
		i++;
		if (i >= sizeof(lgnPtsSvcs[type])) return;
	}

	switch(cmd)
	{
	case CMD_LGNSVC_NAMECHANGE:
		UpdateName(clientH, cNewName);
		LegionCash(player->m_cAccountName, type, i, true);
		break;

	case CMD_LGNSVC_STATRESET:
		StatReset(clientH);
		LegionCash(player->m_cAccountName, type, i, true);
		break;

	/*case CMD_LGNSVC_EXTRACTGEM:
		if (!RemoveGem(clientH, true)) return;
		LegionCash(player->m_cAccountName, type, i, true);
		break;

	case CMD_LGNSVC_DESTROYGEM:
		if (!RemoveGem(clientH, false)) return;
		LegionCash(player->m_cAccountName, type, i, true);
		break;*/

	case CMD_LGNSVC_TOWNCHANGE:
		if (player->m_side == ARESDEN) {
			ChangeNation(clientH, ELVINE);
			if (!ChangeNation(clientH, ELVINE)) return;
		}else if (player->m_side == ELVINE) {
			if (!ChangeNation(clientH, ARESDEN)) return;
		}else return;
		LegionCash(player->m_cAccountName, type, i, true);
		break;

	case CMD_LGNSVC_GOLD10:
		if(!GetLegionItemCount(clientH, "Gold", 10000))
			return;
		LegionCash(player->m_cAccountName, type, i, true);
		break;
	case CMD_LGNSVC_GOLD100:
		if(!GetLegionItemCount(clientH, "Gold", 100000))
			return;
		LegionCash(player->m_cAccountName, type, i, true);
		break;
	case CMD_LGNSVC_TOKEN1:
		if(!GetLegionItemCount(clientH, "LegionCoin", lgnPtsSvcs[type][i].price))
			return;
		LegionCash(player->m_cAccountName, type, i, true);
		break;
	case CMD_LGNSVC_TOKEN10:
		if(!GetLegionItemCount(clientH, "LegionCoin", lgnPtsSvcs[type][i].price))
			return;
		LegionCash(player->m_cAccountName, type, i, true);
		break;
	case CMD_LGNSVC_TOKEN100:
		if(!GetLegionItemCount(clientH, "LegionCoin", lgnPtsSvcs[type][i].price))
			return;
		LegionCash(player->m_cAccountName, type, i, true);
		break;

	case CMD_LGNSVC_WARRIORPLATE:
	case CMD_LGNSVC_BMAGEPLATE:
	case CMD_LGNSVC_BMAGECHAIN:
	case CMD_LGNSVC_MAGECHAIN:
	case CMD_LGNSVC_WARRIORHAUBERK:
	case CMD_LGNSVC_MAGEHAUBERK:
	case CMD_LGNSVC_WARRIORHOSE:
	case CMD_LGNSVC_MAGEHOSE:
	case CMD_LGNSVC_WARRIORCAP:
	case CMD_LGNSVC_MAGECAP:
	case CMD_LGNSVC_WARRIORWINGHELM:
	case CMD_LGNSVC_MAGEWINGHELM:
	case CMD_LGNSVC_LGNPLATE:
	case CMD_LGNSVC_LGNCHAIN:
	case CMD_LGNSVC_LGNPLATELEGS:
	case CMD_LGNSVC_LGNHAUBERK:
	case CMD_LGNSVC_LGNWINGHELM:
	case CMD_LGNSVC_LGNWIZCAP:
	case CMD_LGNSVC_LGNWIZHAT:
	case CMD_LGNSVC_LGNHORNHELM:
//	case CMD_LGNSVC_XPSLATE:
	case CMD_LGNSVC_ZERKSLATE:
	case CMD_LGNSVC_MPSLATE:
	//case CMD_LGNSVC_HPSLATE:
	case CMD_LGNSVC_ZEM:
	case CMD_LGNSVC_SOM:
	case CMD_LGNSVC_SOX: 
	case CMD_LGNSVC_SEXCHANGE:
	case CMD_LGNSVC_DYEGOLD:
	case CMD_LGNSVC_DYECRIMRED:
	case CMD_LGNSVC_DYEPINK:
	case CMD_LGNSVC_DYEBLACK:
	case CMD_LGNSVC_KEY:
		if(!GetLegionItem(clientH, cmd, sex))
			return;
		LegionCash(player->m_cAccountName, type, i, true);
		break;

	case CMD_LGNSVC_TRADETOKEN1:
	case CMD_LGNSVC_TRADETOKEN10:
	case CMD_LGNSVC_TRADETOKEN100:
		if(!GetLegionPoints(clientH, lgnPtsSvcs[type][i].price))
			return;
		LegionCash(player->m_cAccountName, type, i, false);
		break;

	case CMD_LGNSVC_TRADECANCEL:
	case CMD_LGNSVC_TRADEIMC:
	case CMD_LGNSVC_TRADEICESTORM:
	case CMD_LGNSVC_TRADEMFS:
	case CMD_LGNSVC_TRADEBS:
	case CMD_LGNSVC_TRADEESW:
		if(!GetSpellBack(clientH, cmd))
			return;
		LegionCash(player->m_cAccountName, type, i, true);
		break;

	case CMD_LGNSVC_CONT2:
		player->m_iContribution += 2;
		SendNotifyMsg(NULL, player->m_handle, NOTIFY_CONTRIBUTION);
		LegionCash(player->m_cAccountName, type, i, true);
		break;
	case CMD_LGNSVC_CONT20:
		player->m_iContribution+= 10;
		SendNotifyMsg(NULL, player->m_handle, NOTIFY_CONTRIBUTION);
		LegionCash(player->m_cAccountName, type, i, true);
		break;

	/*case CMD_LGNSVC_REP10:
	case CMD_LGNSVC_REP100:
		player->m_reputation += 10 * lgnPtsSvcs[cmd].price;
		LegionCash(player->m_cAccountName, type, i, true);
		break;*/

	default:
		return;
	}

	bSendMsgToLS(MSGID_REQUEST_SAVEPLAYERDATA, clientH);
}

void CGame::LegionCash(char * account, char type, char i, bool Type)
{
	char data[20];
	WORD * wp; char * cp;

	cp = (char *)data;
	memcpy(cp, account, 10);
	cp += 10;
	
	*cp = type;
	cp++;

	*cp = i;
	cp++;

	if(!Type) bSendMsgToLS(MSGID_ADDCASH, NULL, NULL, data);
	else if(Type) bSendMsgToLS(MSGID_SUBCASH, NULL, NULL, data);
}

