void CGame::StatReset(int clientH)
{
	CClient * player = m_pClientList[clientH];

	player->SetStr(10);
	player->m_iVit = 10;
	player->m_iHP = player->GetMaxHP();
	player->SetDex(10);
	player->SetInt(10);
	CheckMagicInt(clientH, true);
	player->SetMag(10);
	player->m_iCharisma = 10;
	player->m_iLU_Pool = player->m_iLevel*3 + 10;

	player->ValidateSkills(FALSE);

	ValidateEquipment(clientH);

	SendNotifyMsg(NULL, clientH, NOTIFY_SETTING_SUCCESS);
}

void CGame::UpdateName(int clientH, char *NewName)
{
	char cData[20];
	DWORD * dwp;
	WORD * wp;
	char * cp;
	CClient * player = m_pClientList[clientH];

	ZeroMemory(player->m_cCharName, sizeof(player->m_cCharName));
	memcpy(player->m_cCharName, NewName, 10);

	dwp  = (DWORD *)(cData + INDEX4_MSGID);
	*dwp = MSGID_NOTIFY;
	wp   = (WORD *)(cData + INDEX2_MSGTYPE);
	*wp  = NOTIFY_UPDATECHARACTERNAME;

	cp = (char *)(cData + INDEX2_MSGTYPE + 2);
	memcpy(cp, player->m_cCharName, 10);
	cp += 10;

	switch (player->m_pXSock->iSendMsg(cData, 16)) {
	case XSOCKEVENT_QUENEFULL:
	case XSOCKEVENT_SOCKETERROR:
	case XSOCKEVENT_CRITICALERROR:
	case XSOCKEVENT_SOCKETCLOSED:
					PutLogList("test 36");
		DeleteClient(clientH, TRUE, TRUE);
		return;
	}

	SendEventToNearClient_TypeA(clientH, OWNERTYPE_PLAYER, MSGID_EVENT_MOTION, OBJECTNULLACTION, NULL, NULL, NULL);
}

bool CGame::ChangeNation(int clientH, Side side)
{
	char cData[20];
	DWORD * dwp, dwAttribute;
	WORD * wp;
	char * cp;
	CClient * player = m_pClientList[clientH];
	CItem * pItem;
	short sTempID;
	int iEraseReq;

	if(player->m_side == side || m_bIsCrusadeMode) return false;
	
	if ((side == ARESDEN || side == ELVINE) && (player->m_side == ARESDEN || player->m_side == ELVINE)) {
		for(int i = 0; i < MAXITEMS; i++)
		if (player->m_pItemList[i] != NULL) {
			sTempID = -1;
			if (player->m_pItemList[i]->m_sIDnum == 402 || player->m_pItemList[i]->m_sIDnum == 429) continue;
			if ((player->m_pItemList[i]->m_sIDnum >= 403 && player->m_pItemList[i]->m_sIDnum <= 426) || (player->m_pItemList[i]->m_sIDnum >= 431 && player->m_pItemList[i]->m_sIDnum <= 438)) {
				if (player->m_side == ARESDEN) sTempID = player->m_pItemList[i]->m_sIDnum+2;
				else if (player->m_side == ELVINE) sTempID = player->m_pItemList[i]->m_sIDnum-2;
				if ((player->m_pItemList[i]->m_dwAttribute >> 24) != NULL) {
					if (player->m_side == ARESDEN) player->m_pItemList[i]->m_cItemColor += 2;
					else if (player->m_side == ELVINE) player->m_pItemList[i]->m_cItemColor -= 2;
				}
			}
			else if ((player->m_pItemList[i]->m_sIDnum >= 400 && player->m_pItemList[i]->m_sIDnum <= 401) || (player->m_pItemList[i]->m_sIDnum >= 427 && player->m_pItemList[i]->m_sIDnum <= 428)) {
				if (player->m_side == ARESDEN) sTempID = player->m_pItemList[i]->m_sIDnum+1;
				else if (player->m_side == ELVINE) sTempID = player->m_pItemList[i]->m_sIDnum-1;
			}else if (player->m_pItemList[i]->m_sIDnum >= 1055 && player->m_pItemList[i]->m_sIDnum <= 1074) {
				if ((player->m_pItemList[i]->m_dwAttribute >> 28) != NULL) {
					sTempID = player->m_pItemList[i]->m_sIDnum;
					if (player->m_side == ARESDEN) player->m_pItemList[i]->m_cItemColor += 2;
					else if (player->m_side == ELVINE) player->m_pItemList[i]->m_cItemColor -= 2;
				}
			}
			if (sTempID != -1) {
				if (player->m_pItemList[i]->m_cTouchEffectType != ITET_UNIQUE_OWNER || 
					player->m_pItemList[i]->m_sTouchEffectValue1 != m_pClientList[player->m_handle]->m_sCharIDnum1 || 
					player->m_pItemList[i]->m_sTouchEffectValue2 != m_pClientList[player->m_handle]->m_sCharIDnum2 || 
					player->m_pItemList[i]->m_sTouchEffectValue3 != m_pClientList[player->m_handle]->m_sCharIDnum3) continue;
				pItem = new class CItem;
				pItem->InitItemAttr((ItemID)sTempID);
				pItem->m_cTouchEffectType = ITET_UNIQUE_OWNER;
				pItem->m_sTouchEffectValue1 = m_pClientList[player->m_handle]->m_sCharIDnum1;
				pItem->m_sTouchEffectValue2 = m_pClientList[player->m_handle]->m_sCharIDnum2;
				pItem->m_sTouchEffectValue3 = m_pClientList[player->m_handle]->m_sCharIDnum3;
				pItem->m_dwAttribute = player->m_pItemList[i]->m_dwAttribute;
				pItem->m_dwTempItemAttr = player->m_pItemList[i]->m_dwTempItemAttr;
				pItem->m_cItemColor = player->m_pItemList[i]->m_cItemColor;
				ItemDepleteHandler(player->m_handle,i,0,1);
				_bAddClientItemList(player->m_handle, pItem, &iEraseReq);
				if (iEraseReq == 1) delete pItem;
				SendNotifyMsg(NULL, player->m_handle, NOTIFY_ITEMDEPLETED_ERASEITEM, i, 1, NULL, NULL);
				SendItemNotifyMsg(player->m_handle, NOTIFY_ITEMOBTAINED, pItem, NULL, NULL);
			}
		}

		for(int i = 0; i < MAXBANKITEMS; i++)
		if (player->m_pItemInBankList[i] != NULL) {
			sTempID = -1;
			if (player->m_pItemInBankList[i]->m_sIDnum == 402 || player->m_pItemInBankList[i]->m_sIDnum == 429) continue;
			if ((player->m_pItemInBankList[i]->m_sIDnum >= 403 && player->m_pItemInBankList[i]->m_sIDnum <= 426) || (player->m_pItemInBankList[i]->m_sIDnum >= 431 && player->m_pItemInBankList[i]->m_sIDnum <= 438)) {
					if (player->m_side == ARESDEN) sTempID = player->m_pItemInBankList[i]->m_sIDnum+2;
					else if (player->m_side == ELVINE) sTempID = player->m_pItemInBankList[i]->m_sIDnum-2;
				if ((player->m_pItemInBankList[i]->m_dwAttribute >> 24) != NULL) {
					if (player->m_side == ARESDEN) player->m_pItemInBankList[i]->m_cItemColor += 2;
					else if (player->m_side == ELVINE) player->m_pItemInBankList[i]->m_cItemColor -= 2;
				}
			}
			else if ((player->m_pItemInBankList[i]->m_sIDnum >= 400 && player->m_pItemInBankList[i]->m_sIDnum <= 401) || (player->m_pItemInBankList[i]->m_sIDnum >= 427 && player->m_pItemInBankList[i]->m_sIDnum <= 428)) {
				if (player->m_side == ARESDEN) sTempID = player->m_pItemInBankList[i]->m_sIDnum+1;
				else if (player->m_side == ELVINE) sTempID = player->m_pItemInBankList[i]->m_sIDnum-1;
			}else if (player->m_pItemInBankList[i]->m_sIDnum >= 1055 && player->m_pItemInBankList[i]->m_sIDnum <= 1074) {
				if ((player->m_pItemInBankList[i]->m_dwAttribute >> 28) != NULL) {
					sTempID = player->m_pItemInBankList[i]->m_sIDnum;
					if (player->m_side == ARESDEN) player->m_pItemInBankList[i]->m_cItemColor += 2;
					else if (player->m_side == ELVINE) player->m_pItemInBankList[i]->m_cItemColor -= 2;
				}
			}
			if (sTempID != -1) {
				if (player->m_pItemInBankList[i]->m_cTouchEffectType != ITET_UNIQUE_OWNER || 
					player->m_pItemInBankList[i]->m_sTouchEffectValue1 != player->m_sCharIDnum1 || 
					player->m_pItemInBankList[i]->m_sTouchEffectValue2 != player->m_sCharIDnum2 || 
					player->m_pItemInBankList[i]->m_sTouchEffectValue3 != player->m_sCharIDnum3) continue;
				pItem = new class CItem;
				pItem->InitItemAttr((ItemID)sTempID);
				pItem->m_dwAttribute = player->m_pItemInBankList[i]->m_dwAttribute;
				pItem->m_dwTempItemAttr = player->m_pItemInBankList[i]->m_dwTempItemAttr;
				pItem->m_cItemColor = player->m_pItemInBankList[i]->m_cItemColor;
				pItem->m_cTouchEffectType = ITET_UNIQUE_OWNER;
				pItem->m_sTouchEffectValue1 = player->m_sCharIDnum1;
				pItem->m_sTouchEffectValue2 = player->m_sCharIDnum2;
				pItem->m_sTouchEffectValue3 = player->m_sCharIDnum3;
				player->m_pItemInBankList[i] = NULL;
				bSetItemToBankItem(player->m_handle, pItem, TRUE);
			}
		}
	}

	player->m_side = side;
	ZeroMemory(player->m_cLocation, sizeof(player->m_cLocation));
	strcpy(player->m_cLocation, sideMap[side]);
	player->SetSideFlag(side);

	dwp  = (DWORD *)(cData + INDEX4_MSGID);
	*dwp = MSGID_NOTIFY;
	wp   = (WORD *)(cData + INDEX2_MSGTYPE);
	*wp  = NOTIFY_RESPONSE_HUNTMODE;

	cp = (char *)(cData + INDEX2_MSGTYPE + 2);
	memcpy(cp, player->m_cLocation, 10);
	cp += 10;

	switch (player->m_pXSock->iSendMsg(cData, 16)) {
	case XSOCKEVENT_QUENEFULL:
	case XSOCKEVENT_SOCKETERROR:
	case XSOCKEVENT_CRITICALERROR:
	case XSOCKEVENT_SOCKETCLOSED:
					PutLogList("test 37");
		DeleteClient(clientH, TRUE, TRUE);
		return false;
	}

	SendEventToNearClient_TypeA(clientH, OWNERTYPE_PLAYER, MSGID_EVENT_MOTION, OBJECTNULLACTION, NULL, NULL, NULL);

	player->WithdrawFromGuild();

	if(player->GetParty()) 
		m_partyMgr.RemoveFromParty( player );
	
	RequestTeleportHandler(player->m_handle, 0);

	return true;
}

