void CGame::ReqRepairAll(int iClientH)
{
	DWORD goldprice = 0, contprice = 0;
	char itemCategory;
	CItem * item;
	double d1, d2;

	if (m_pClientList[iClientH] == NULL) return;
	if (m_pClientList[iClientH]->m_bIsInitComplete == FALSE) return;
	
	for (int i = 0; i < MAXITEMS; i++) {
		item = m_pClientList[iClientH]->m_pItemList[i];
		if (!item) continue;

		if (item->m_sItemEffectType == ITEMEFFECTTYPE_ADDEFFECT && item->m_sItemEffectValue1 == 15) continue;

		itemCategory = item->m_cCategory;

		if(itemCategory < 1 || itemCategory > 50 || (itemCategory > 13 && itemCategory < 43)) continue; 

		if(item->m_wMaxLifeSpan == 0 || item->m_wCurLifeSpan == item->m_wMaxLifeSpan) continue;

		if (item->m_sIDnum >= 1030 && item->m_sIDnum <= 1074) {
			if ((item->m_wMaxLifeSpan-item->m_wCurLifeSpan)/2000 <= 0) contprice++;
			else contprice += (item->m_wMaxLifeSpan-item->m_wCurLifeSpan)/2000;
		}else if (item->m_wCurLifeSpan == 0) {
			goldprice += item->m_wPrice / 2;
		}else {
			d1 = item->m_wMaxLifeSpan - item->m_wCurLifeSpan;
			d2 = (d1 / item->m_wMaxLifeSpan) * 0.5f;
			goldprice += d2 * item->m_wPrice;
		}
	}

	SendNotifyMsg(NULL, iClientH, NOTIFY_REPAIRALLPRICE, goldprice, contprice);
}

void CGame::ReqRepairAllConfirmHandler(int iClientH, int quotedPrice)
{
	DWORD goldCount, contCount, startGoldCount, startContCount, price = 0, contprice = 0, i, dwTemp;
	char itemCategory;
	CItem * item;
	double d1, d2;

	if (m_pClientList[iClientH] == NULL) return;
	if (m_pClientList[iClientH]->m_bIsInitComplete == FALSE) return;

	goldCount = startGoldCount = dwGetItemCount(iClientH, "Gold");
	contCount = startContCount = m_pClientList[iClientH]->m_iContribution;

	for (i = 0; i < MAXITEMS; i++) {
		item = m_pClientList[iClientH]->m_pItemList[i];
		if (!item) continue;

		if (item->m_sItemEffectType == ITEMEFFECTTYPE_ADDEFFECT && item->m_sItemEffectValue1 == 15) continue;

		itemCategory = item->m_cCategory;

		if(itemCategory < 1 || itemCategory > 50 || (itemCategory > 13 && itemCategory < 43)) continue; 

		if(item->m_wMaxLifeSpan == 0 || item->m_wCurLifeSpan == item->m_wMaxLifeSpan) continue;
		
		if (item->m_sIDnum >= 1030 && item->m_sIDnum <= 1074) {
			if ((item->m_wMaxLifeSpan-item->m_wCurLifeSpan)/2000 <= 0) contprice++;
			else contprice += (item->m_wMaxLifeSpan-item->m_wCurLifeSpan)/2000;
		}
		else if (item->m_wCurLifeSpan == 0) {
			price += item->m_wPrice / 2;
		}
		else {
			d1 = item->m_wMaxLifeSpan - item->m_wCurLifeSpan;
			d2 = (d1 / item->m_wMaxLifeSpan) * 0.5f;
			price += d2 * item->m_wPrice; 
		}
	}
	
	if (goldCount == 0 && price > 0 && contCount == 0 && contprice > 0) return;
	if (goldCount < price) SendNotifyMsg(NULL, iClientH, NOTIFY_NOTENOUGHGOLD);

	if (price != quotedPrice) {
		SendNotifyMsg(NULL, iClientH, NOTIFY_REPAIRALLPRICE, price);
	}
	else {
		for (i = 0; i < MAXITEMS; i++) {
			item = m_pClientList[iClientH]->m_pItemList[i];
			if (!item) continue;

			itemCategory = item->m_cCategory;

			if(itemCategory < 1 || itemCategory > 50 || (itemCategory > 13 && itemCategory < 43)) continue; 

			if(item->m_wMaxLifeSpan == 0 || item->m_wCurLifeSpan == item->m_wMaxLifeSpan) continue;

			if (item->m_sIDnum >= 1030 && item->m_sIDnum <= 1074) {
				if ((item->m_wMaxLifeSpan-item->m_wCurLifeSpan)/2000 <= 0) dwTemp = 1;
				else dwTemp = (item->m_wMaxLifeSpan-item->m_wCurLifeSpan)/2000;
				if (contCount >= dwTemp) {
					item->m_wCurLifeSpan = item->m_wMaxLifeSpan;
					contCount -= dwTemp;
				}else {
					item->m_wCurLifeSpan += contCount*2000;
					if (item->m_wCurLifeSpan > item->m_wMaxLifeSpan) item->m_wCurLifeSpan = item->m_wMaxLifeSpan;
					contCount = 0;
				}
			}
			else if (price < goldCount) {
				item->m_wCurLifeSpan = item->m_wMaxLifeSpan;
				goldCount -= price;
			} 
			else {
				d1 = item->m_wMaxLifeSpan * goldCount;
				d2 = item->m_wPrice / 2;
				item->m_wCurLifeSpan += d1/d2;
				goldCount = 0;
			}
			SendNotifyMsg(NULL, iClientH, NOTIFY_ITEMREPAIRED, i, item->m_wCurLifeSpan, NULL, NULL);
		}
		m_pClientList[iClientH]->m_iContribution = contCount;
		if (startContCount != contCount) SendNotifyMsg(NULL, iClientH, NOTIFY_CONTRIBUTION);
		m_stCityStatus[m_pClientList[iClientH]->m_side].iFunds += (startGoldCount - goldCount);
		SetItemCount(iClientH, "Gold", goldCount);
		iCalcTotalWeight(iClientH);
	}
}

